        public static void DemoThread()
        {
            Thread sampleThread1 = new Thread(new ThreadStart(SampleThread1));
            Thread sampleThread2 = new Thread(new ThreadStart(SampleThread2));
            Thread sampleThread3 = new Thread(new ThreadStart(SampleThread3));

            sampleThread1.Start();
            sampleThread2.Start();
            sampleThread3.Start();
            
            Console.ReadKey();
            sampleThread1.Abort();
            sampleThread2.Abort();
            sampleThread3.Abort();
        }

        static void SampleThread1()
        {
            while (true)
            {
                Console.WriteLine("Thread 1");
                Thread.Sleep(5000);
            }
